package ua; import ua.repository.*; import ua.models.*; import java.util.*; public class Main{ public static void main(String[]args){ StudentRepository sr=new StudentRepository(); for(int i=1;i<=1000;i++) sr.add(new Student(i,"S"+i,18+(i%10),100+(i%5))); long t1=System.nanoTime(); long c1=sr.getAll().stream().filter(s->s.getAge()>20).count(); long t2=System.nanoTime(); long t3=System.nanoTime(); long c2=sr.getAll().parallelStream().filter(s->s.getAge()>20).count(); long t4=System.nanoTime(); System.out.println("seq:"+(t2-t1)/1_000_000+" ms, par:"+(t4-t3)/1_000_000+" ms"); } }