package ua.models; import java.util.Comparator; public class Group implements Comparable<Group>{ private int id; private String title; private int course; public Group(int id,String title,int course){this.id=id;this.title=title;this.course=course;} public int getId(){return id;} public String getTitle(){return title;} public int getCourse(){return course;} public int compareTo(Group o){return Integer.compare(id,o.id);} public static Comparator<Group> byTitle=Comparator.comparing(Group::getTitle); public static Comparator<Group> byCourse=Comparator.comparingInt(Group::getCourse);} 