package ua.models; import java.util.Comparator; public class Student implements Comparable<Student>{ private int id; private String name; private int age; private int groupId; public Student(int id,String name,int age,int groupId){this.id=id;this.name=name;this.age=age;this.groupId=groupId;} public int getId(){return id;} public String getName(){return name;} public int getAge(){return age;} public int getGroupId(){return groupId;} public int compareTo(Student o){return Integer.compare(id,o.id);} public static Comparator<Student> byName=Comparator.comparing(Student::getName); public static Comparator<Student> byAge=Comparator.comparingInt(Student::getAge); public static Comparator<Student> byGroup=Comparator.comparingInt(Student::getGroupId);} 