package ua.repository; import ua.models.Group; import java.util.*; public class GroupRepository extends GenericRepository<Group>{ public GroupRepository(){super(Group::getId);} public List<Group> findByTitle(String s){return storage.values().stream().filter(g->g.getTitle().toLowerCase().contains(s.toLowerCase())).toList();} public List<Group> findByCourseRange(int a,int b){return storage.values().stream().filter(g->g.getCourse()>=a&&g.getCourse()<=b).toList();} }