package ua.repository; import ua.models.Movie; import java.util.*; public class MovieRepository extends GenericRepository<Movie>{ public MovieRepository(){super(Movie::getId);} public List<Movie> findByTitle(String s){return storage.values().stream().filter(m->m.getTitle().toLowerCase().contains(s.toLowerCase())).toList();} public List<Movie> findByYearRange(int a,int b){return storage.values().stream().filter(m->m.getYear()>=a&&m.getYear()<=b).toList();} }