package ua.repository; import ua.models.Student; import java.util.*; public class StudentRepository extends GenericRepository<Student>{ public StudentRepository(){super(Student::getId);} public List<Student> findByName(String n){return storage.values().stream().filter(s->s.getName().equals(n)).toList();} public List<Student> findByAgeRange(int a,int b){return storage.values().stream().filter(s->s.getAge()>=a&&s.getAge()<=b).toList();} public List<Student> findByGroup(int g){return storage.values().stream().filter(s->s.getGroupId()==g).toList();} }